var mySwiper = new Swiper(".tuiguang", {
    slidesPerView: "auto",
    centeredSlides: !0,
    watchSlidesProgress: !0,


    onProgress: function(a) {
        var b, c, d;
        for (b = 0; b < a.slides.length; b++) c = a.slides[b],
        d = c.progress,
        scale = 1 - Math.min(Math.abs(.2 * d), 1),
        es = c.style,
        es.opacity = 1 - Math.min(Math.abs(d / 2), 1),
        es.webkitTransform = es.MsTransform = es.msTransform = es.MozTransform = es.OTransform = es.transform = "translate3d(0px,0," + -Math.abs(300 * d) + "px)"
    },
});
