var phpurl = $('#stows').attr("skin");
$(function() {
	$("#like").click(function() {
		var b = $(this),
			a = b.attr("rel");
		$.ajax({
			type: "POST",
			url: phpurl+"/likes.php?type=likes",
			data: "id=" + a,
			cache: !1,
			success: function(a) {
				b.html(a)
			}
		});
		return !1
	})
});
